class Solution(object):
    def findSubstring(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        
        # length of string, and number of words
        L = len(s)
        n = len(words)
        
        # deal with trivial case: no words
        if n == 0:
            return []
        
        # length of each word
        l = len(words[0])
        
        # deal with trivial case: string too short
        if (L < l*n):
            return []
        
        # dictionary that stores how many times each word appears
        dic = {}
        # create or update dictionary for each word
        for i in range(n):
            if not words[i] in dic:
                dic[words[i]] = 1
            else:
                dic[words[i]] = dic[words[i]] + 1                
        
        # total length of the concatenation
        nl = n * l   
        # list of the positions of found matching
        ans = []
        # traverse the string
        for i in range(L-l+1):  
            # take current substring
            ss = s[i:i+nl]
            # make a copy of the dictionary
            # each occurance of word is found reduce its count by 1
            dic_i = dic.copy()
            # flag if problems are found
            flag = False
            # check if each substring of length l is a word
            for j in range(n):
                # current substring of length l
                sss = ss[j*l:(j+1)*l] 
                # check if current substring is not a word
                # or it has already occured for allowed times
                if (not sss in dic_i) or (dic_i[sss] == 0) :
                    # current substring is not a legal concatenation
                    # update flag and break current loop
                    flag = True
                    break
                else:
                    # if current substting is a word
                    # and remaining occurance count is larger than 0
                    dic_i[sss] = dic_i[sss] - 1
            
            # if no problems have been found
            if not flag:
                # check if all remaining occurance counts are 0
                all_match = True
                for j in dic_i:
                    # if remaining occurance count not 0
                    if dic_i[j] != 0:
                        # update flag and break loop
                        all_match = False
                        break
                # if all remaining occurance counts are 0
                # the current substring is a concatenation
                if all_match:
                    # add current position to answer list
                    ans.append(i)
            else:
                # if found problem, current substring is not a concatenation
                # continue searching to next position
                continue            
        
        # return answer list
        return ans                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             

