class Solution(object):
    def findSubstring(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        L = len(s)
        n = len(words)
        
        if n == 0:
            return []
        
        l = len(words[0])
        
        if (L < l*n):
            return [] 
       
        dic = {}
        for i in range(n):
            if not words[i] in dic:
                dic[words[i]] = 1
            else:
                dic[words[i]] = dic[words[i]] + 1                
    
        nl = n * l   
        ans = []
        for i in range(L-l+1):  
            ss = s[i:i+nl]
            dic_i = dic.copy()
            flag = False
            for j in range(n):
                sss = ss[j*l:(j+1)*l] 
                if (not sss in dic_i) or (dic_i[sss] == 0) :
                    flag = True
                    break
                else:
                    dic_i[sss] = dic_i[sss] - 1
            
            if not flag:
                all_match = True
                for j in dic_i:
                    if dic_i[j] != 0:
                        all_match = False
                        break
                if all_match:
                    ans.append(i)
            else:
                continue            
        
        return ans           
        
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
